import {
  takeLatest, put, all, call,
} from 'redux-saga/effects';


import UserActionTypes from './user.types';
import {
  googleSignInFailure, googleSignInSuccess, emailSignInFailure, emailSignInSuccess,
} from './user.action';
import { auth, googleProvider, createUserProfileDocument } from '../../firebase/filrebase.util';


export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShoot = yield userRef.get();
    yield put(googleSignInSuccess({ id: userSnapShoot.id, ...userSnapShoot.data() }));
  } catch (error) {
    yield put(googleSignInFailure(error));
  }
}


export function* signInWithEmail({ payload: { email, password } }) {
  try {
    const { user } = auth.signInWithEmailAndPassword(email, password);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShoot = yield userRef.get();
    yield put(emailSignInSuccess({ id: userSnapShoot.id, ...userSnapShoot.data() }));
  } catch (error) {
    yield put(emailSignInFailure(error));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* onEmailSignInStart() {
  yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signInWithEmail);
}


export function* userSagas() {
  yield all([call(onGoogleSignInStart)], [call(onEmailSignInStart)]);
}
