const Spinner = ({ isLoading, ...other }) => ( // 如果loadining, 就放spinner
  <SpinnerOverlay>
    <SpinnerContainer />
  </SpinnerOverlay>
);

//   : <Component {...other} />)// 不然就higher order component, 该放什么正常放

